import React from 'react'
import { useState, useEffect } from 'react'
import UserMapper from '../mapper/UserMapper'
import UserKeyDataMapper from '../mapper/UserKeyDataMapper'

/**
 * Custom Hook to Fetch User & Key Data
 * @param { number } id
 * @return { UserData, KeyData }
 */

function useFetch(id) {
    const [error, setError] = useState(null)
    const [isLoaded, setIsLoaded] = useState(false)
    const [userData, setUserData] = useState({})
    const [keyData, setKeyData] = useState({})

    // useEffect(() => {
    //     fetch(`http://localhost:3000/user/${id}`)
    //         //** MOCK LINK **
    //         // fetch(`../user/${id}/user.json`)
    //         .then((res) => res.json())
    //         .then(
    //             ({ data }) => {
    //                 setUserData(UserMapper.convertToUser(data))
    //                 setKeyData(UserKeyDataMapper.convertToKeyData(data))
    //                 setIsLoaded(true)
    //             },
    //             (error) => {
    //                 console.log(error)
    //                 setError(true)
    //             }
    //         )
    // }, [id])

    useEffect(() => {
        async function fetchUser() {
            try {
                const response = await fetch(`http://localhost:3000/user/${id}`)
                const { data } = await response.json()
                setUserData(UserMapper.convertToUser(data))
                setKeyData(UserKeyDataMapper.convertToKeyData(data))
            } catch (err) {
                console.log(err)
                setError(true)
            } finally {
                setIsLoaded(true)
            }
        }
        fetchUser()
    }, [error, id])

    if (error) {
        return <div>Erreur</div>
    } else if (!isLoaded) {
        return <div>Chargement...</div>
    } else {
        return {
            userData,
            keyData,
            isLoaded,
            error,
        }
    }
}

export default useFetch
