import * as d3 from 'd3'
import { useState, useEffect } from 'react'

function Weight({ activitiesData }) {
    const [SvgWeight, setSvgWeight] = useState()

    useEffect(() => {
        const SvgWeightCreation = () => {
            const svg = d3
                .select('.weight-box')
                .append('svg')
                .attr('width', 825)
                .attr('height', 320)
                .style('background', '#FBFBFB')
                .style('border-radius', '5px')

            const graph = svg
                .append('g')
                .attr('width', 685)
                .attr('height', 140)
                .attr('transform', 'translate(50, 110)')

            const groupKilo = graph
                .append('g')
                .attr('transfrom', 'translate(10, 0')

            const groupCalories = graph
                .append('g')
                .attr('transfrom', 'translate(30, 0')

            const rectKilo = groupKilo
                .selectAll('rect')
                .data(activitiesData)
                .enter()
                .append('rect')
                .attr('width', 7)
                .attr('height', function (d) {
                    return d.kilogram
                })
                .attr('fill', '#282D30')
                .attr('x', function (d, i) {
                    return i * 110
                })

            // const rectCalories = groupCalories
            //     .selectAll('rect')
            //     .data(activitiesData)
            //     .enter()
            //     .append('rect')
            //     .attr('width', 7)
            //     .attr('height', function (d) {
            //         return d.calories
            //     })
            //     .attr('fill', '#E60000')
            //     .attr('x', function (d, i) {
            //         return i * 110
            //     })
        }

        setSvgWeight(SvgWeightCreation)
    }, [SvgWeight, activitiesData])

    return (
        <div className="rounded-md col-span-3 weight-box">
            {console.log(activitiesData)}
        </div>
    )
}

export default Weight
