import React from 'react'
import UserMapper from '../mapper/UserMapper'
import { useState, useEffect } from 'react'
import UserKeyDataMapper from '../mapper/UserKeyDataMapper'
import UserActivitiesData from '../core/UserActivitiesData'
import UserActivitiesMapper from '../mapper/UserActivitiesMapper'

function useFetch(userId) {
    const [error, setError] = useState(null)
    const [isLoaded, setIsLoaded] = useState(false)
    const [userData, setUserData] = useState({})
    const [keyData, setKeyData] = useState({})
    const [activitiesData, setActivitiesData] = useState({})

    // Remarque : le tableau vide de dépendances [] indique
    // que useEffect ne s’exécutera qu’une fois, un peu comme
    // componentDidMount()
    useEffect(() => {
        fetch(`http://localhost:3000/user/${userId}`)
            .then((res) => res.json())
            .then(
                ({ data }) => {
                    //console.log(data)
                    setUserData(UserMapper.convertToUser(data))
                    setKeyData(UserKeyDataMapper.convertToKeyData(data))
                },
                // Remarque : il faut gérer les erreurs ici plutôt que dans
                // un bloc catch() afin que nous n’avalions pas les exceptions
                // dues à de véritables bugs dans les composants.
                (error) => {
                    setError(error)
                }
            )
        fetch(`http://localhost:3000/user/${userId}/activity`)
            .then((res) => res.json())
            .then(
                ({ data }) => {
                    setActivitiesData(
                        UserActivitiesMapper.convertToActivities(data)
                    )
                    setIsLoaded(true)
                },
                // Remarque : il faut gérer les erreurs ici plutôt que dans
                // un bloc catch() afin que nous n’avalions pas les exceptions
                // dues à de véritables bugs dans les composants.
                (error) => {
                    setError(error)
                }
            )
        //console.log(userData)
    }, [])

    if (error) {
        return <div>Erreur</div>
    } else if (!isLoaded) {
        return <div>Chargement...</div>
    } else {
        return { userData, keyData, activitiesData, isLoaded, error }
    }
}

export default useFetch
